INSERT INTO `reply_post` VALUES (00040300000000000001,00040100000000000001,NULL,00040500000000000002,'2013-01-13','16:00:00','{\"全文\":\"建议： 感冒和天气有很大关系，要注意防寒保暖，提高免疫力\\r\\n\"}',NULL,49,-1,NULL,0,NULL),(00040300000000000002,00040100000000000001,NULL,00040500000000000003,'2013-04-16','19:37:00','{\"全文\":\"建议： 你好。建议做些适当的运动，多喝水，还可以到医院注射免疫球蛋白增加体内抵抗能力。\\r\\n\"}',NULL,35,-1,NULL,0,NULL),(00040300000000000003,00040100000000000001,NULL,00040500000000000004,'2013-07-01','14:38:00','{\"全文\":\"建议： 你好，如果一般轻微的感冒可以自行去按照医导的嘱咐下服用感冒药，如果严重的话就需要去医院做检查，进一步治疗．\\r\\n\"}',NULL,9,-1,NULL,0,NULL),(00040300000000000004,00040100000000000001,NULL,00040500000000000005,'2013-07-03','17:01:00','{\"全文\":\"建议： 你好，感冒是很常见的，主要是上呼吸道防疫功能弱的原因，平时注意多锻炼，注意饮食不要偏食。就会减少感冒次数及减轻严重程度。\\r\\n\"}',NULL,9,-1,NULL,0,NULL),(00040300000000000005,00040100000000000001,NULL,00040500000000000006,'2013-07-05','09:51:00','{\"全文\":\"建议： 您好，感冒轻微时，可以多喝水，如果还不见好的话建议到医院治疗。建议平时饮食规律 ，养成良好的生活习惯和卫生习惯以及良好的作息规律，注意一定不要熬夜，积极锻炼身体，增强体质，提高自身免疫力。多吃水果、蔬菜等。此外，保持心情愉快亦很重要。\\r\\n\"}',NULL,12,-1,NULL,0,NULL),(00040300000000000006,00040100000000000001,NULL,00040500000000000007,'2013-07-09','14:23:00','{\"全文\":\"建议： 患者您好， 我来给你解答 \\r\\n【总结】：情况比较急严重你去最好本地的医院找个专家，也可以到上海等大城市。癫痫的治疗应该从癫痫发作的机制入手，常见的癫痫病治疗方法有药物治疗，手术治疗等。 \\r\\n【治疗】：药物可以通过降低大脑兴奋性来控制癫痫发作，但药物治疗一般需要长期服药，而且副作用也较大。而且并不是所有的癫痫病人都适合手术治疗。 \\r\\n【原因】：癫痫发作是由于神经元异常放电所致，只有修复了受损的神经元细胞，才能彻底杜绝癫痫的发作。建议去专业的癫痫病医院，采用修复受损神经元细 胞的治疗方案针对受损神经元进行一个靶向的有效修复，可以完全修复受损神经元细胞，彻底杜绝癫痫的发作。\\r\\n\"}',NULL,8,-1,NULL,0,NULL),(00040300000000000007,00040100000000000001,NULL,00040500000000000007,'2013-07-11','10:37:00','{\"全文\":\"建议： \\r\\n患者您好， 我来给你解答 \\r\\n【总结】：情况比较急严重你去最好本地的医院找个专家，也可以到上海等大城市。癫痫的治疗应该从癫痫发作的机制入手，常见的癫痫病治疗方法有药物治疗，手术治疗等。 \\r\\n【治疗】：药物可以通过降低大脑兴奋性来控制癫痫发作，但药物治疗一般需要长期服药，而且副作用也较大。而且并不是所有的癫痫病人都适合手术治疗。 \\r\\n【原因】：癫痫发作是由于神经元异常放电所致，只有修复了受损的神经元细胞，才能彻底杜绝癫痫的发作。建议去专业的癫痫病医院，采用修复受损神经元细胞的治疗方案针对受损神经元进行一个靶向的有效修复，可以完全修复受损神经元细胞，彻底杜绝癫痫的发作。\\r\\n\"}',NULL,4,-1,NULL,0,NULL),(00040300000000000008,00040100000000000001,NULL,00040500000000000007,'2013-07-15','15:31:00','{\"全文\":\"建议： 是 抑 郁 症 ？ \\r\\n我来给你从抑郁症临床角度解答问题： 情况比较严重你去最好去本地的医院找 个专家治疗，也可以来上海。 \\r\\n偶尔的抑郁情绪可以自我调节，下面介绍几种方法，希望对你有所帮助。 \\r\\n一、自然陶冶法。人生在世，喜怒哀乐皆有。当你平时心情不好的时候可在晚饭后到你家附近的公园散散步，痛快地玩一玩，对改变你的不良的情绪肯定会有帮助。 \\r\\n二、语言调节法。通过语言既可抑制人的愉快情绪，也可抑制做的忧愁与苦闷。即使不出声的内部语言也能使人调节自己的情绪。 \\r\\n三、注意转移法。注意转移法就是把自己的注意力从一种情绪状态中转移到另一种能引起其他情绪状态的事物上。 \\r\\n四、思维转移法。把思维活动从消极的、不愉快的情绪上转移到另外的情绪状态中去，从而摆脱消极的、不良的情绪的缠绕称为思维转移法，这是 从端正心态的角度解答抑郁症如何自我调节的问题。 \\r\\n五、行动转移法。行动转移法是将某些情绪转化为行动的方法，这种方法是从以实际行动宣泄情绪的角度来解答抑郁症如何自我调节的问题。\\r\\n\"}',NULL,5,-1,NULL,0,NULL),(00040300000000000009,00040100000000000001,NULL,00040500000000000008,'2013-09-06','15:06:00','{\"全文\":\"建议： 乙肝小三阳要根据自身的病情进行检查，如果病情比较严重，就需要半个月或者一个月，如果病情不是很严重，可以三个月检查一次。由于乙肝小三阳危害比较大，应该及时的治疗。 \\r\\n乙肝小三阳应该定期的做乙肝五项检查，乙肝ＤＮＡ检查，乙肝病毒耐药检查和肝功能检查。。 _-__\\r\\n\"}',NULL,2,-1,NULL,0,NULL),(00040300000000000010,00040100000000000001,NULL,00040500000000000009,'2013-01-12','15:27:00','{\"全文\":\"体质的问题，最好吃草药，调理一段时间，等体质好了，各种病也不容易侵袭你了。特别是女性，坐月子一定要好好做，那可是女人体质的分水岭，既可以改善先前体质，也可以调理以后的身体状况。\"}',NULL,0,-1,NULL,0,NULL),(00040300000000000011,00040100000000000001,NULL,00040500000000000010,'2013-01-16','22:43:00','{\"全文\":\"可以喝姜茶啊，最好少吃药，自然好最好了可以喝姜茶啊，最好少吃药，自然好最好了\"}',NULL,0,-1,NULL,0,NULL),(00040300000000000012,00040100000000000001,NULL,00040500000000000011,'2013-03-14','15:27:00','{\"全文\":\"因为着凉吗？多喝热水这样会快点好，平常多穿衣服。\"}',NULL,0,-1,NULL,0,NULL),(00040300000000000013,00040100000000000001,NULL,00040500000000000012,'2013-05-03','11:07:00','{\"全文\":\"建议先看医生，平时多喝水多运动，注意卫生，保持室内空气流通！\"}',NULL,0,-1,NULL,0,NULL),(00040300000000000014,00040100000000000001,NULL,00040500000000000013,'2013-05-19','15:11:00','{\"全文\":\"一般来说想你这个年纪，普通感冒鼻塞，流鼻涕是可以不看医生的，多喝开水，保持室内通风，尽量避免到人多的地方。如果是引起哮喘的话，建议去就医。感冒主要是机体抵抗力下降所导致的，所以平时就要增强抵抗力，多运动，饮食也要均衡，不偏食，天气变化时要注意保暖，特别对于女性来说生理周期时抵抗力要比平时差些。\"}',NULL,0,-1,NULL,0,NULL),(00040300000000000015,00040100000000000001,NULL,00040500000000000014,'2013-05-22','09:21:00','{\"全文\":\"感冒没别的巧，多喝水，保持空气流通，尽量少去人多的地方。宝宝、孕妇、老人这些人群抵抗力相对偏低，平时注意保暖，饮食方便也要注意。\"}',NULL,0,-1,NULL,0,NULL),(00040